
var re = /(?:^\s*|[}{\(\);,\n]\s*)(?:import|(?:import|export|module)\s+[^"'\(\)\n;]+\s+from)\s*['"]([^'"]+)['"]/g

/**
 * Expose `imports`.
 */

module.exports = imports;

/**
 * Parse imports in `str`.
 *
 * @param {String} str
 * @param {Function} [fn]
 * @return {Array}
 * @api public
 */

function imports(str, fn) {
  if (fn) return map(str, fn);
  var ret = [];
  var m;

  str = stripComments(str);
  while (m = re.exec(str)) {
    ret.push({
      string: m[0],
      path: m[1],
      index: m.index
    });
  }

  return ret;
}

/**
 * Map imports.
 */

function map(str, fn) {
  imports(str).forEach(function(r){
    str = str.replace(r.string, fn(r));
  });

  return str;
}

/**
 * Strip comments.
 */

function stripComments(str) {
  return str.replace(/\/\*[\S\s]*?\*\/|\/\/.*/g, '');
}
