
var imports = require('./');

[
  'import * from "module"',
  'import "module"',
  'import {} from "module"',
  'module x from "module"',
  'export * from "module"',
  'export {} from "module"',
].forEach(function (expression) {
  it(expression, function () {
    var match = imports(expression);
    match.length.should.equal(1);
    match[0].path.should.equal('module');
  })
})
